import React, { useRef, useEffect, useState } from "react";
import { useMemoria } from "./memoryContext";
function FragmentacionInterna() {
  const salida = [
    { dispositivo: "Pantalla", velocidad: "2KBs" },
    { dispositivo: "Impresora", velocidad: "1 - 5 Kbs" },
    { dispositivo: "Linea de comunicaciones", velocidad: "30Bps - 20MBps" },
  ];
  const { espacio, setEspacio } = useMemoria();
  // const kb = useRef();
  const [procesos, setProcesos] = useState([]);
  const addDispositivo = (dispositivo) => {
    return setProcesos((p) => [...p, { ...dispositivo }]);
  };
  useEffect(() => {
    const espacioContainer = espacio;
    for (let index = 0; index < procesos.length; index++) {
      console.log(procesos.velocidad);
      espacioContainer[index] = {
        ocupado: true,
        proceso: procesos.velocidad,
      };
    }
    setEspacio((e) => [...espacioContainer]);
  }, [procesos]);
  return (
    <div>
      <h1 className="titulo">Dispositivos de Salida:</h1>
      {salida.map((i) => {
        return (
          <>
            <div className="element">
              <h1>{i.dispositivo}</h1>
              <button
                onClick={() => {
                  addDispositivo(i);
                }}
              >
                Conectar
              </button>
            </div>
          </>
        );
      })}
    </div>
  );
}

export default FragmentacionInterna;
