import React, { useState, useContext, useEffect } from "react";

const MemoriaContext = React.createContext();
export function useMemoria() {
  return useContext(MemoriaContext);
}
function MemoryContext({ children }) {
  const initialArray = [
    {
      ocupado: false,
      proceso: "",
    },
    {
      ocupado: false,
