    {
      ocupado: false,
      proceso: "",
    },
    {
      ocupado: false,
      proceso: "",
    },
    {
      ocupado: false,
      proceso: "",
    },
    {
      ocupado: false,
      proceso: "",
    },
    {
      ocupado: false,
      proceso: "",
    },
  ];
  const [espacio, setEspacio] = useState(initialArray);
  const initialize = () => {
    setEspacio((p) => [...initialArray]);
  };
  return (
    <MemoriaContext.Provider value={{ espacio, setEspacio, initialize }}>
      {children}
    </MemoriaContext.Provider>
  );
}
export default MemoryContext;
